mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,CODE_NAME FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT ,CODE_NAME FROM BOARD JOIN COM_CODE ON BOARD_TYPE = CODE_ID WHERE CODE_TYPE = 'menu'
BOARD_TYPE = #{item}
) WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

mapper.delete:
DELETE FROM BOARD 
 WHERE  BOARD_NUM = #{boardNum}
 AND BOARD_TYPE = #{boardType}

